import React, { useState } from 'react';
import { createRecipe } from '../services/api';

function UploadRecipe() {
  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [instructions, setInstructions] = useState('');
  const [author, setAuthor] = useState('');
  const [token, setToken] = useState(''); // Implement authentication to get the token

  const handleSubmit = async (e) => {
    e.preventDefault();
    const recipe = { title, ingredients: ingredients.split(','), instructions, author };
    try {
      await createRecipe(recipe, token);
      alert('Recipe uploaded successfully!');
    } catch (error) {
      console.error('Error uploading recipe:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Upload Recipe</h2>
      <label>
        Title:
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
      </label>
      <label>
        Ingredients (comma separated):
        <input type="text" value={ingredients} onChange={(e) => setIngredients(e.target.value)} required />
      </label>
      <label>
        Instructions:
        <textarea value={instructions} onChange={(e) => setInstructions(e.target.value)} required />
      </label>
      <label>
        Author:
        <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} required />
      </label>
      <button type="submit">Upload Recipe</button>
    </form>
  );
}

export default UploadRecipe;
